import React, { useState } from "react";
import './style.css'; // ◊ß◊ï◊ë◊• CSS ◊ú◊¢◊ô◊¶◊ï◊ë
reportWebVitals();
const DeliveryForm = () => {

    const [tableData, setTableData] = useState([
        { clientName: "", address: "", deliveryDate: "", startTime: "", lastTime: "", status: "" },
        
    ]);
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState("");
    const [viewMode, setViewMode] = useState('list'); // 'list' or 'map'

    // ◊ò◊ô◊§◊ï◊ú ◊ë◊î◊¢◊ú◊ê◊™ ◊ß◊ï◊ë◊•
    const handleFileUpload = (e) => {
        const uploadedFile = e.target.files[0];
    
        if (!uploadedFile || !uploadedFile.name.endsWith(".csv")) {
            alert("◊ê◊†◊ê ◊î◊¢◊ú◊î ◊ß◊ï◊ë◊• CSV ◊ë◊ú◊ë◊ì.");
            return;
        }
        setFile(uploadedFile);
    };

    // ◊î◊ï◊°◊§◊™ ◊©◊ï◊®◊î ◊ó◊ì◊©◊î ◊ú◊ò◊ë◊ú◊î
    const addRow = () => {
        setTableData([...tableData, { clientName: "", address: "", deliveryDate: "", startTime: "", lastTime: "", status: "" }]);
    };

    // ◊û◊ó◊ô◊ß◊™ ◊©◊ï◊®◊î ◊û◊î◊ò◊ë◊ú◊î
    const removeRow = (index) => {
        setTableData(tableData.filter((_, i) => i !== index));
    };

    // ◊©◊ô◊†◊ï◊ô ◊¢◊®◊õ◊ô◊ù ◊ë◊©◊ì◊ï◊™ ◊î◊ò◊ë◊ú◊î
    const handleInputChange = (index, e) => {
        const newTableData = [...tableData];
        newTableData[index][e.target.name] = e.target.value;
        setTableData(newTableData);
    };
    const toggleView = () => {
        setViewMode(viewMode === 'list' ? 'map' : 'list');

    // ‚úÖ ◊ë◊ì◊ô◊ß◊™ ◊™◊ß◊ô◊†◊ï◊™ - ◊î◊ê◊ù ◊õ◊ú ◊î◊©◊ì◊ï◊™ ◊û◊ú◊ê◊ô◊ù
    const isFormValid = () => {
        return tableData.every(row =>
            row.clientName.trim() &&
            row.address.trim() &&
            row.deliveryDate.trim() &&
            row.startTime.trim() &&
            row.lastTime.trim() &&
            row.status.trim()
        );
    };

    // ◊©◊ú◊ô◊ó◊™ ◊î◊†◊™◊ï◊†◊ô◊ù ◊ú◊©◊®◊™ ◊ê◊ï ◊ô◊¶◊ô◊®◊™ ◊ß◊ï◊ë◊• CSV
    const handleSubmit = async (e) => {
        e.preventDefault();

        // ◊ë◊ì◊ô◊ß◊î ◊ê◊ù ◊õ◊ú ◊î◊©◊ì◊ï◊™ ◊û◊ú◊ê◊ô◊ù
        if (!isFormValid()) {
            alert("◊ô◊© ◊ú◊û◊ú◊ê ◊ê◊™ ◊õ◊ú ◊î◊©◊ì◊ï◊™ ◊ú◊§◊†◊ô ◊î◊©◊ú◊ô◊ó◊î.");
            return;
        }

        const formData = new FormData();

        if (file) {
            // ‚úÖ ◊ê◊ù ◊ô◊© ◊ß◊ï◊ë◊• - ◊©◊ï◊ú◊ó◊ô◊ù ◊ú◊©◊®◊™ Flask
            formData.append("file", file);
            
            try {
                const response = await fetch("http://localhost:5000/upload", {
                    method: "POST",
                    body: formData,
                });

                if (!response.ok) {
                    throw new Error("◊©◊í◊ô◊ê◊î ◊ë◊™◊í◊ï◊ë◊î ◊û◊î◊©◊®◊™.");
                }

                const result = await response.json();
                setMessage(result.message);

                // ‚úÖ ◊ê◊ô◊§◊ï◊° ◊î◊ò◊ï◊§◊° ◊ú◊ê◊ó◊® ◊©◊ú◊ô◊ó◊î ◊û◊ï◊¶◊ú◊ó◊™
                setFile(null);
                setTableData([{ clientName: "", address: "", deliveryDate: "", startTime: "", lastTime: "", status: "" }]);
            } catch (error) {
                console.error("◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊ó◊™ ◊î◊ß◊ï◊ë◊•:", error);
                setMessage("◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊ó◊™ ◊î◊ß◊ï◊ë◊•.");
            }
        } else {
            // ‚úÖ ◊ê◊ù ◊ê◊ô◊ü ◊ß◊ï◊ë◊• - ◊©◊ï◊û◊®◊ô◊ù ◊†◊™◊ï◊†◊ô◊ù ◊õ-CSV ◊ï◊û◊ï◊®◊ô◊ì◊ô◊ù ◊ê◊ï◊™◊ï
            const csvContent =
                "\uFEFF◊©◊ù ◊î◊ú◊ß◊ï◊ó,◊õ◊™◊ï◊ë◊™,◊™◊ê◊®◊ô◊ö ◊û◊©◊ú◊ï◊ó,◊©◊¢◊™ ◊î◊™◊ó◊ú◊î,◊©◊¢◊™ ◊°◊ô◊ï◊ù,◊°◊ò◊ò◊ï◊°\n" +
                tableData
                    .map((row) => `${row.clientName},${row.address},${row.deliveryDate},${row.startTime},${row.lastTime},${row.status}`)
                    .join("\n");
            
            const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
            const link = document.createElement("a");
            link.href = URL.createObjectURL(blob);
            link.download = "shipping_data.csv";
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            
            setMessage("◊î◊†◊™◊ï◊†◊ô◊ù ◊†◊©◊û◊®◊ï ◊õ◊ß◊ï◊ë◊• CSV!");

            // ‚úÖ ◊ê◊ô◊§◊ï◊° ◊î◊ò◊ï◊§◊° ◊ú◊ê◊ó◊® ◊©◊û◊ô◊®◊™ ◊î◊ß◊ï◊ë◊•
            setTableData([{ clientName: "", address: "", deliveryDate: "", startTime: "", lastTime: "", status: "" }]);
        }
    };

    return (
        <div className="container">
            <h2>üì¶ ◊ò◊ï◊§◊° ◊î◊ñ◊†◊™ ◊û◊©◊ú◊ï◊ó◊ô◊ù</h2>
            <form onSubmit={handleSubmit}>
                <input type="file" accept=".csv" onChange={handleFileUpload} />
                <table>
                    <thead>
                        <tr>
                            <th>◊©◊ù ◊î◊ú◊ß◊ï◊ó</th>
                            <th>◊õ◊™◊ï◊ë◊™</th>
                            <th>◊™◊ê◊®◊ô◊ö ◊û◊©◊ú◊ï◊ó</th>
                            <th>◊©◊¢◊™ ◊î◊™◊ó◊ú◊î</th>
                            <th>◊©◊¢◊™ ◊°◊ô◊ï◊ù</th>
                            <th>◊°◊ò◊ò◊ï◊°</th>
                            <th>üóëÔ∏è</th>
                        </tr>
                    </thead>
                    <tbody>
                        {tableData.map((row, index) => (
                            <tr key={index}>
                                <td><input type="text" name="clientName" value={row.clientName} onChange={(e) => handleInputChange(index, e)} /></td>
                                <td><input type="text" name="address" value={row.address} onChange={(e) => handleInputChange(index, e)} /></td>
                                <td><input type="date" name="deliveryDate" value={row.deliveryDate} onChange={(e) => handleInputChange(index, e)} /></td>
                                <td><input type="time" name="startTime" value={row.startTime} onChange={(e) => handleInputChange(index, e)} /></td>
                                <td><input type="time" name="lastTime" value={row.lastTime} onChange={(e) => handleInputChange(index, e)} /></td>
                                <td><input type="text" name="status" value={row.status} onChange={(e) => handleInputChange(index, e)} /></td>
                                <td><button type="button" onClick={() => removeRow(index)}>x</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <button type="button" onClick={addRow}>‚ûï ◊î◊ï◊°◊£ ◊©◊ï◊®◊î</button>
                <button type="submit">üì§ ◊©◊ú◊ó ◊†◊™◊ï◊†◊ô◊ù</button>
            </form>
            {message && <p className="message">{message}</p>}
            <button onClick={toggleView}>
            {viewMode === 'list' ? 'View on Map' : 'View as List'}
          </button>
          
        </div>
    );
};
}
export default DeliveryForm;
