    import React, { useState } from "react";
    import './style.css'; // קובץ CSS לעיצוב

    const DeliveryForm = () => {
    const [tableData, setTableData] = useState([
    { clientName: "", address: "", deliveryDate: "", startTime: "", lastTime: "", status: "" },
    ]);
    const [file, setFile] = useState(null);
    const [message, setMessage] = useState("");

    // טיפול בהעלאת קובץ
    const handleFileUpload = (e) => {
    const uploadedFile = e.target.files[0];

    if (!uploadedFile || (!uploadedFile.name.endsWith(".csv") && !uploadedFile.name.endsWith(".json"))) {
        alert("אנא העלה קובץ מסוג CSV או JSON בלבד.");
        return;
    }
    setFile(uploadedFile);
    };

    // הוספת שורה חדשה לטבלה
    const addRow = () => {
    setTableData([...tableData, { clientName: "", address: "", deliveryDate: "", startTime: "", lastTime: "", status: "" }]);
    };

    // מחיקת שורה מהטבלה
    const removeRow = (index) => {
    setTableData(tableData.filter((_, i) => i !== index));
    };

    // שינוי ערכים בשדות הטבלה
    const handleInputChange = (index, e) => {
    const newTableData = [...tableData];
    newTableData[index][e.target.name] = e.target.value;
    setTableData(newTableData);
    };

    // בדיקת תקינות - האם כל השדות מלאים
    const isFormValid = () => {
    return tableData.every(row =>
        row.clientName.trim() &&
        row.address.trim() &&
        row.deliveryDate.trim() &&
        row.startTime.trim() &&
        row.lastTime.trim() &&
        row.status.trim()
    );
    };
            const handleSubmit = async (e) => {
                e.preventDefault();
            
                // אם יש קובץ, נשלח אותו לשרת
                if (file) {
                    const formData = new FormData();
                    formData.append("file", file);
                    console.log(file);
            
                    try {
                        const response = await fetch("http://localhost:5000/upload", {
                            method: "POST",
                            body: formData,
                        });
            
                        if (!response.ok) {
                            throw new Error("שגיאה בתגובה מהשרת.");
                        }
            
                        const result = await response.json();
                        setMessage(result.message);
            
                        // איפוס הטופס לאחר שליחה מוצלחת
                        setFile(null);
                        setTableData([{ clientName: "", address: "", deliveryDate: "", startTime: "", lastTime: "", status: "" }]);
                    } catch (error) {
                        console.error("שגיאה בשליחת הקובץ:", error);
                        setMessage("שגיאה בשליחת הקובץ.");
                    }
                    return; // יציאה מהפונקציה לאחר שליחת הקובץ
                }
            
                // אם אין קובץ, נשלח את הנתונים כקובץ CSV לשרת
                const csvContent =
                    "\uFEFFשם הלקוח,כתובת,תאריך משלוח,שעת התחלה,שעת סיום,סטטוס\n" +
                    tableData
                        .map((row) => `${row.clientName},${row.address},${row.deliveryDate},${row.startTime},${row.lastTime},${row.status}`)
                        .join("\n");
            
                const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
                const formData = new FormData();
                formData.append("file", new File([blob], "shipping_data.csv"));
            
                try {
                    const response = await fetch("http://localhost:5000/upload", {
                        method: "POST",
                        body: formData,
                    });
            
                    if (!response.ok) {
                        throw new Error("שגיאה בתגובה מהשרת.");
                    }
            
                    const result = await response.json();
                    setMessage(result.message);
            
                    // איפוס הטופס לאחר שליחה מוצלחת
                    setTableData([{ clientName: "", address: "", deliveryDate: "", startTime: "", lastTime: "", status: "" }]);
                } catch (error) {
                    console.error("שגיאה בשליחת הנתונים:", error);
                    setMessage("שגיאה בשליחת הנתונים.");
                }
            };
            
            return (
            <div className="container">
                <h2>📦 טופס הזנת משלוחים</h2>
                <form onSubmit={handleSubmit}>
                    <input type="file" accept=".csv,.json" onChange={handleFileUpload} />
                    {!file && (
                        <table>
                            <thead>
                                <tr>
                                    <th>שם הלקוח</th>
                                    <th>כתובת</th>
                                    <th>תאריך משלוח</th>
                                    <th>שעת התחלה</th>
                                    <th>שעת סיום</th>
                                    <th>סטטוס</th>
                                    <th>🗑️</th>
                                </tr>
                            </thead>
                            <tbody>
                                {tableData.map((row, index) => (
                                    <tr key={index}>
                                        <td><input type="text" name="clientName" value={row.clientName} onChange={(e) => handleInputChange(index, e)} /></td>
                                        <td><input type="text" name="address" value={row.address} onChange={(e) => handleInputChange(index, e)} /></td>
                                        <td><input type="date" name="deliveryDate" value={row.deliveryDate} onChange={(e) => handleInputChange(index, e)} /></td>
                                        <td><input type="time" name="startTime" value={row.startTime} onChange={(e) => handleInputChange(index, e)} /></td>
                                        <td><input type="time" name="lastTime" value={row.lastTime} onChange={(e) => handleInputChange(index, e)} /></td>
                                        <td><input type="text" name="status" value={row.status} onChange={(e) => handleInputChange(index, e)} /></td>
                                        <td><button type="button" onClick={() => removeRow(index)}>x</button></td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    )}
                    <button type="button" onClick={addRow}>➕ הוסף שורה</button>
                    <button type="submit">📤 שלח נתונים</button>
                </form>
                {message && <p className="message">{message}</p>}
            </div>
            );
            };

    export default DeliveryForm;
