import React from "react";

const DeliveryTable = ({ tableData, setTableData }) => {
    const addRow = () => {
        setTableData([...tableData, { clientName: "", address: "", deliveryDate: "", startTime: "", lastTime: "", status: "" }]);
    };

    const removeRow = (index) => {
        setTableData(tableData.filter((_, i) => i !== index));
    };


    const handleInputChange = (index, e) => {
        const { name, value } = e.target;
        const newTableData = [...tableData];
        newTableData[index][name] = value;

        // בדיקה אם שעת התחלה קטנה משעת סיום
        if (name === "startTime" || name === "lastTime") {
            const startTime = newTableData[index].startTime;
            const lastTime = newTableData[index].lastTime;

            if (startTime && lastTime && startTime >= lastTime) {
                alert("שעת התחלה חייבת להיות קטנה משעת סיום.");
                return;
            }
        }

        setTableData(newTableData);
    };

    return (
        <table>
            <thead>
                <tr>
                    <th>שם הלקוח</th>
                    <th>כתובת</th>
                    <th>תאריך משלוח</th>
                    <th>שעת התחלה</th>
                    <th>שעת סיום</th>
                    <th>סטטוס</th>
                    <th>🗑️</th>
                </tr>
            </thead>
            <tbody>
                {tableData.map((row, index) => (
                    <tr key={index}>
                        <td><input type="text" name="clientName" value={row.clientName} onChange={(e) => handleInputChange(index, e)} /></td>
                        <td><input type="text" name="address" value={row.address} onChange={(e) => handleInputChange(index, e)} /></td>
                        <td><input type="date" name="deliveryDate" value={row.deliveryDate} onChange={(e) => handleInputChange(index, e)} /></td>
                        <td><input type="time" name="startTime" value={row.startTime} onChange={(e) => handleInputChange(index, e)} /></td>
                        <td><input type="time" name="lastTime" value={row.lastTime} onChange={(e) => handleInputChange(index, e)} /></td>
                        <td><input type="text" name="status" value={row.status} onChange={(e) => handleInputChange(index, e)} /></td>
                        <td><button type="button" onClick={() => removeRow(index)}>x</button></td>
                    </tr>
                ))}
            </tbody>
            <tfoot>
                <tr>
                    <td colSpan="7">
                        <button type="button" onClick={addRow}>➕ הוסף שורה</button>
                    </td>
                </tr>
            </tfoot>
        </table>
    );
};

export default DeliveryTable;
