                console.error("◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊ó◊™ ◊î◊ß◊ï◊ë◊•:", error);
                setMessage("◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊ó◊™ ◊î◊ß◊ï◊ë◊•.");
            }
